import { useState } from "react";
import { FiSearch } from "react-icons/fi";
import './styles.css';
import api from "./services/api";

function App() {

  async function handleSearch(){
    if(input === ''){
      alert('Preencha algum cep!')
      return;
    }

    try{
      const response = await api.get({input} + '/json');

    }catch{
      alert("Ops! CEP não encontrado.")
      setInput('')
    }
  }

  const [input, setInput] = useState('')
  return (
    <div className="container">
      <h1 className="title">Buscador CEP</h1>

      <div className="containerInput">
        <input type="text" placeholder="Digite seu CEP" value={input} onChange={(e)=> setInput(e.target.value)}></input>
        <button className="buttonSearch" onClick={handleSearch}><FiSearch size={25} color="FFF"/></button>
      </div>

      <main className="main">
        <h2>CEP: 47850-000</h2>
        <span>Rua Pará</span>
        <span>Complemento: Quadra 07 Lote 19</span>
        <span>Centro</span>
        <span>Luís Eduardo Magalhães - BA</span>
      </main>
    </div>
  );
}

export default App;
